mapper.select:
SELECT
-- 级联表的字段 ,huiyishi.huiyishi_uuid_number as huiyishiUuidNumber ,huiyishi.huiyishi_address as huiyishiAddress ,huiyishi.huiyishi_size as huiyishiSize ,huiyishi.huiyishi_types as huiyishiTypes ,huiyishi.zhuangtai_types as zhuangtaiTypes ,jiaoshi.jiaoshi_name as jiaoshiName ,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber ,jiaoshi.jiaoshi_phone as jiaoshiPhone ,jiaoshi.jiaoshi_photo as jiaoshiPhoto ,jiaoshi.jiaoshi_delete as jiaoshiDelete
FROM huiyishi_shenqing a left JOIN huiyishi huiyishi ON a.huiyishi_id = huiyishi.id left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id
and a.id in
#{item}
and a.jiaoshi_id = #{params.jiaoshiId}
and a.huiyishi_id = #{params.huiyishiId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.huiyishi_shenqing_yesno_types = #{params.huiyishiShenqingYesnoTypes}
and a.huiyishi_id IS NOT NULL
and huiyishi.huiyishi_uuid_number like CONCAT('%',#{params.huiyishiUuidNumber},'%')
and huiyishi.huiyishi_address like CONCAT('%',#{params.huiyishiAddress},'%')
and huiyishi.huiyishi_size like CONCAT('%',#{params.huiyishiSize},'%')
and huiyishi.huiyishi_types = #{params.huiyishiTypes}
and huiyishi.zhuangtai_types = #{params.zhuangtaiTypes}
and a.jiaoshi_id IS NOT NULL
and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')
and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')
and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')
and jiaoshi.jiaoshi_delete >= #{params.jiaoshiDeleteStart}
and jiaoshi.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}
and jiaoshi.jiaoshi_delete = #{params.jiaoshiDelete}
order by a.${params.orderBy} desc
