mapper.select:
SELECT
-- 级联表的字段 ,jiaoshi.jiaoshi_name as jiaoshiName ,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber ,jiaoshi.jiaoshi_phone as jiaoshiPhone ,jiaoshi.jiaoshi_photo as jiaoshiPhoto ,jiaoshi.jiaoshi_delete as jiaoshiDelete ,yinzhang.yinzhang_uuid_number as yinzhangUuidNumber ,yinzhang.yinzhang_name as yinzhangName
FROM yinzhang_shenqing a left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id left JOIN yinzhang yinzhang ON a.yinzhang_id = yinzhang.id
and a.id in
#{item}
and a.jiaoshi_id = #{params.jiaoshiId}
and a.yinzhang_id = #{params.yinzhangId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yinzhang_shenqing_yesno_types = #{params.yinzhangShenqingYesnoTypes}
and a.jiaoshi_id IS NOT NULL
and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')
and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')
and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')
and jiaoshi.jiaoshi_delete >= #{params.jiaoshiDeleteStart}
and jiaoshi.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}
and jiaoshi.jiaoshi_delete = #{params.jiaoshiDelete}
and a.yinzhang_id IS NOT NULL
and yinzhang.yinzhang_uuid_number like CONCAT('%',#{params.yinzhangUuidNumber},'%')
and yinzhang.yinzhang_name like CONCAT('%',#{params.yinzhangName},'%')
order by a.${params.orderBy} desc
