mapper.select:
SELECT
-- 级联表的字段 ,bangongshi.bangongshi_name as bangongshiName ,bangongshi.bangongshi_address as bangongshiAddress ,jiaoshi.jiaoshi_name as jiaoshiName ,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber ,jiaoshi.jiaoshi_phone as jiaoshiPhone ,jiaoshi.jiaoshi_photo as jiaoshiPhoto ,jiaoshi.jiaoshi_delete as jiaoshiDelete
FROM bangongshi_shenqing a left JOIN bangongshi bangongshi ON a.bangongshi_id = bangongshi.id left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id
and a.id in
#{item}
and a.jiaoshi_id = #{params.jiaoshiId}
and a.bangongshi_id = #{params.bangongshiId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.bangongshi_shenqing_yesno_types = #{params.bangongshiShenqingYesnoTypes}
and a.bangongshi_id IS NOT NULL
and bangongshi.bangongshi_name like CONCAT('%',#{params.bangongshiName},'%')
and bangongshi.bangongshi_address like CONCAT('%',#{params.bangongshiAddress},'%')
and a.jiaoshi_id IS NOT NULL
and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')
and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')
and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')
and jiaoshi.jiaoshi_delete >= #{params.jiaoshiDeleteStart}
and jiaoshi.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}
and jiaoshi.jiaoshi_delete = #{params.jiaoshiDelete}
order by a.${params.orderBy} desc
