mapper.select:
SELECT
-- 级联表的字段 ,bangongwuzi.bangongwuzi_uuid_number as bangongwuziUuidNumber ,bangongwuzi.bangongwuzi_name as bangongwuziName ,bangongwuzi.bangongwuzi_types as bangongwuziTypes ,jiaoshi.jiaoshi_name as jiaoshiName ,jiaoshi.jiaoshi_id_number as jiaoshiIdNumber ,jiaoshi.jiaoshi_phone as jiaoshiPhone ,jiaoshi.jiaoshi_photo as jiaoshiPhoto ,jiaoshi.jiaoshi_delete as jiaoshiDelete
FROM bangongwuzi_shenqing a left JOIN bangongwuzi bangongwuzi ON a.bangongwuzi_id = bangongwuzi.id left JOIN jiaoshi jiaoshi ON a.jiaoshi_id = jiaoshi.id
and a.id in
#{item}
and a.jiaoshi_id = #{params.jiaoshiId}
and a.bangongwuzi_id = #{params.bangongwuziId}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.bangongwuzi_shenqing_yesno_types = #{params.bangongwuziShenqingYesnoTypes}
and a.bangongwuzi_id IS NOT NULL
and bangongwuzi.bangongwuzi_uuid_number like CONCAT('%',#{params.bangongwuziUuidNumber},'%')
and bangongwuzi.bangongwuzi_name like CONCAT('%',#{params.bangongwuziName},'%')
and bangongwuzi.bangongwuzi_types = #{params.bangongwuziTypes}
and a.jiaoshi_id IS NOT NULL
and jiaoshi.jiaoshi_name like CONCAT('%',#{params.jiaoshiName},'%')
and jiaoshi.jiaoshi_id_number like CONCAT('%',#{params.jiaoshiIdNumber},'%')
and jiaoshi.jiaoshi_phone like CONCAT('%',#{params.jiaoshiPhone},'%')
and jiaoshi.jiaoshi_delete >= #{params.jiaoshiDeleteStart}
and jiaoshi.jiaoshi_delete <= #{params.jiaoshiDeleteEnd}
and jiaoshi.jiaoshi_delete = #{params.jiaoshiDelete}
order by a.${params.orderBy} desc
